import React from "react";
import ReactDOM from "react-dom/client";
import './style.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause, faRepeat, faChevronUp, faChevronDown, faPlus, faMinus } from '@fortawesome/free-solid-svg-icons'

function Button(value, id, action) {
    return(
        <button
        type="button"
        id={id}
        onClick={action}
        >
            {value}
        </button>
    )
}

class Clock extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentState: "session",
            session: 25,
            break: 5,
            minutes: 25,
            seconds: 0 ,
            timerId: null
        };
        this.resetTimer=this.resetTimer.bind(this);
        this.btnStartPause=this.btnStartPause.bind(this);
    }
    btnStartPause() {
        return !!this.state.timerId ? this.pauseTimer() : this.startTimer()
    }

    btnStartPauseValue() {
        return !!this.state.timerId ? <FontAwesomeIcon icon={faPause} size="lg"/> : <FontAwesomeIcon icon={faPlay} size="lg"/>
    }

    startTimer(){
        const newCurrentState = this.state.currentState == "session" ? "break" : "session" 
        if (!!this.state.timerId){
            this.setState({
                minutes: this.state[newCurrentState],
                currentState: newCurrentState,
                seconds: 0
            })    
        }  
        const timerId = setInterval(() => {
            let {seconds, minutes} = this.state

            if (seconds>0){
                seconds--
            } else {
                if (minutes>0){
                    minutes--
                    seconds=59
                } else {
                    this.pauseTimer()
                    this.startTimer()
                    return
                }
            }
            this.setState({minutes,seconds});
        }, 1000)
        this.setState({timerId})
    }

    pauseTimer () {
        clearInterval(this.state.timerId)
        this.setState({
            timerId: null
        })
    }

    resetTimer(){
        clearInterval(this.state.timerId)
        this.setState({
            currentState: "session",
            session:25,
            break:5,
            minutes:25,
            seconds:0,
            timerId:null
        })
    }

    count(action, stateType){
        const newStateTime = action === "-" ? this.state[stateType]-1 : this.state[stateType]+1
        const newMin = stateType == "session" ? newStateTime : this.state.minutes

        this.setState({
            [stateType] : newStateTime,
            minutes: newMin,
            seconds: 0
        })  
    }

    pad(val){
        return (val > 10 || val==="00") ? val : "0"+val
    }

    render() {
        return (
            <div id="main-container">
                <div id="time-container" className="container">
                    <p id="timer-label">{this.state.currentState}</p>
                    <p id="time-left">{this.pad(this.state.minutes)}:{this.pad(this.state.seconds)}</p>
                    <div className="btn_section">
                        {Button(this.btnStartPauseValue(),"start_stop",(()=>this.btnStartPause()))}
                        {Button(<FontAwesomeIcon icon={faRepeat} size="lg"/>,"reset",this.resetTimer)}
                    </div>    
                </div>
                <div id="session-container" className="small_container container">
                    <button type="button" id="session-increment" disabled={!!this.state.timerId || this.state.session>=60} onClick={()=>this.count("+","session")}><FontAwesomeIcon icon={faPlus} size="lg" /></button>
                    <p id="session-label">session</p>
                    <p id="session-length">{this.state.session}</p>
                    <button type="button" id="session-decrement"  disabled={!!this.state.timerId || this.state.session<=1} onClick={()=>this.count("-","session")}><FontAwesomeIcon icon={faMinus} size="lg"/></button>
                </div>
                <div id="break-container" className="small_container container">
                    <button type="button" id="break-increment" disabled={!!this.state.timerId || this.state.break>=60} onClick={()=>this.count("+","break")}><FontAwesomeIcon icon={faPlus} size="lg"/></button>
                    <p id="break-label">break</p>
                    <p id="break-length">{this.state.break}</p>
                    <button type="button" id="break-decrement" disabled={!!this.state.timerId || this.state.break<=1} onClick={()=>this.count("-","break")}><FontAwesomeIcon icon={faMinus} size="lg"/></button>
                </div>
            </div>
        )
    }
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Clock />);
