import React from "react";
import ReactDOM from "react-dom/client";
import './style.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause, faStop, faChevronUp, faChevronDown } from '@fortawesome/free-solid-svg-icons'

class Clock extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentState: "session",
            session: 25,
            break: 5,
            minutes: 25,
            seconds: 0 ,
            timerId: null
        };
        this.startTimer = this.startTimer.bind(this);
        this.pauseTimer=this.pauseTimer.bind(this);
        this.stopTimer=this.stopTimer.bind(this);
    }

    startTimer(){
        const newCurrentState = this.state.currentState == "session" ? "break" : "session" 
        if (!!this.state.timerId){
            this.setState({
                minutes: this.state[newCurrentState],
                currentState: newCurrentState,
                seconds: 0
            })    
        }

        const timerId = setInterval(() => {
            let {seconds, minutes} = this.state

            if (seconds>0){
                seconds--
            } else {
                if (minutes>0){
                    minutes--
                    seconds=59
                } else {
                    this.pauseTimer()
                    this.startTimer()
                    return
                }
            }
            this.setState({minutes,seconds});
        }, 1000)
        this.setState({timerId})
    }

    pauseTimer () {
        clearInterval(this.state.timerId)
        this.setState({
            timerId: null
        })
    }

    stopTimer(){
        clearInterval(this.state.timerId)
        this.setState({
            session:25,
            break:5,
            minutes:25,
            seconds:0,
            timerId:null
        })
    }

    count(action, stateType){
        const newStateTime = action === "-" ? this.state[stateType]-1 : this.state[stateType]+1
        const newMin = stateType == "session" ? newStateTime : this.state.minutes

        this.setState({
            [stateType] : newStateTime,
            minutes: newMin,
            seconds: 0
        })  
    }

    pad(val){
        return val < 10 ? "0"+val  : val
    }

    render() {
        return (
            <div id="main-container">
                <div id="break-container">
                    <button type="button" id="break-increment" disabled={!!this.state.timerId } onClick={()=>this.count("+","break")}><FontAwesomeIcon icon={faChevronUp} size="lg"/></button>
                    <p id="break-label">break</p>
                    <p id="break-length">{this.pad(this.state.break)}</p>
                    <button type="button" id="break-decrement" disabled={!!this.state.timerId || this.state.break<=1} onClick={()=>this.count("-","break")}><FontAwesomeIcon icon={faChevronDown} size="lg"/></button>
                </div>
                <div id="time-container">
                    <p id="timer-label">{this.state.currentState}</p>
                    <p id="time-left">{this.pad(this.state.minutes)}:{this.pad(this.state.seconds)}</p>
                    <button type="button" id="start" disabled={!!this.state.timerId } onClick={this.startTimer}><FontAwesomeIcon icon={faPlay} size="lg"/></button>
                    <button type="button" id="pause" onClick={this.pauseTimer}><FontAwesomeIcon icon={faPause} size="lg"/></button>
                    <button type="button" id="stop" onClick={this.stopTimer}><FontAwesomeIcon icon={faStop} size="lg"/></button>
                </div>
                <div id="session-container">
                    <button type="button" id="session-increment" disabled={!!this.state.timerId} onClick={()=>this.count("+","session")}><FontAwesomeIcon icon={faChevronUp} size="lg" /></button>
                    <p id="session-label">session</p>
                    <p id="session-length">{this.pad(this.state.session)}</p>
                    <button type="button" id="session-decrement" disabled={!!this.state.timerId || this.state.session<=1} onClick={()=>this.count("-","session")}><FontAwesomeIcon icon={faChevronDown} size="lg"/></button>
                </div>
            </div>
        )
    }
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Clock />);
