import React from "react";
import ReactDOM from "react-dom";
import './style.css'

class Clock extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            time_work: 25,
            time_chill: 5,
            minutes: 25,
            seconds: "00",
            timerId: null
        };
        this.startTimer = this.startTimer.bind(this);
        this.pauseTimer=this.pauseTimer.bind(this);
        this.stopTimer=this.stopTimer.bind(this);
    }

    startTimer(type_){
        this.setState({
            timerId: null,
            minutes: this.state[type_],
            seconds: "00"
        })

        const timerId = setInterval(() => {
            let {seconds, minutes} = this.state

            if (seconds>10){
                seconds=seconds-1

            } else if(seconds>0){
                seconds="0"+(seconds-1)
                console.log("show me your")
            } else {
                if (minutes>0){
                    minutes=minutes-1
                    seconds=59
                } else {
                    this.startTimer(type_=="time_work"?"time_chill":"time_work")
                    console.log("this somehow ended")
                }
            }
            this.setState({minutes,seconds});
        }, 1000)

        this.setState({timerId})
    }

    pauseTimer () {
        clearInterval(this.state.timerId)
        this.setState({
            timerId: null
        })
    }
    stopTimer(){
        clearInterval(this.state.timerId)
        this.setState({
            time_work:25,
            time_chill:5,
            minutes:25,
            seconds:"00",
            timerId:null,
        })
    }

    count(action, typeOfTime){
        const newTime = action === "-" ? this.state[typeOfTime]-1 : this.state[typeOfTime]+1
        const newMin = typeOfTime == "time_work" ? newTime : this.state.minutes

        this.setState({
            [typeOfTime] : newTime,
            minutes: newMin
        })  
    }

    render() {
        return (
            <div id="main-container">
                <div id="break-container">
                    {/*Button("-","break-decrement", count)*/}
                    <button type="button" id="break-decrement" disabled={!!this.state.timerId} onClick={()=>this.count("-","time_chill")}>-</button>
                    <p id="break-label">break</p>
                    <p id="break-length">{this.state.time_chill}</p>
                    <button type="button" id="break-increment" disabled={!!this.state.timerId} onClick={()=>this.count("+","time_chill")}>+</button>
                </div>
                <div id="time-container">
                    {/*decrementing by a value of 1 and updating the display every 1000ms*/}
                    <p id="timer-label">time lebel</p>
                    <p id="time-left">{this.state.minutes}:{this.state.seconds}</p>
                    <button type="button" id="start" onClick={this.startTimer}>start</button>
                    <button type="button" id="pause" onClick={this.pauseTimer}>pause</button>
                    <button type="button" id="stop" onClick={this.stopTimer}>stop</button>
                </div>
                <div id="session-container">
                    <button type="button" id="session-decrement" disabled={!!this.state.timerId} onClick={()=>this.count("-","time_work")}>-</button>
                    <p id="session-label">session</p>
                    <p id="session-length">{this.state.time_work}</p>
                    <button type="button" id="session-increment" disabled={!!this.state.timerId} onClick={()=>this.count("+","time_work")}>+</button>
                </div>
            </div>
        )
    }
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Clock />);
